mapper.select:
select *
from(select rownum rnum, num, category, name, dept, empno, subject, content, profile_file, reg_date
	from(
		select *
		from workboard

		order by reg_date desc
		)
	where rownum < = #{end}
	)
where rnum between #{start} and #{end}

mapper.update:
update workboard
set	   category = #{category}, subject = #{subject}, content = #{content}
where  num = #{num}
